use Cola\Config\Config as Config;
use Cola\Libraries\Error as Error;

if (!function_exists('baseUrl')) {
    function baseUrl($url = null)
    {
        if (!is_null($url) && !is_bool($url)) {
            $url = Config::$default['path'] . $url;

            return($url);
        } else {
            return(Config::$default['path']);
        }
    }
}

